import { Package, FileText, Award } from "lucide-react";

const Stats = () => {
  const stats = [
    {
      icon: Package,
      number: "700+",
      label: "Available Materials",
    },
    {
      icon: FileText,
      number: "20K+",
      label: "Issued RFQs",
    },
    {
      icon: Award,
      number: "20+",
      label: "Trusted Suppliers",
    },
  ];

  return (
    <section className="py-16 md:py-24 bg-background rounded-t-3xl rounded-b-3xl">
      <div className="section-container">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8 md:gap-12 max-w-5xl mx-auto">
          {stats.map((stat, index) => {
            const Icon = stat.icon;
            return (
              <div
                key={index}
                className="text-center animate-fade-in"
                style={{ animationDelay: `${index * 0.1}s` }}
              >
                <div className="w-16 h-16 mx-auto mb-4 bg-accent/10 rounded-full flex items-center justify-center">
                  <Icon className="w-8 h-8 text-accent" strokeWidth={1.5} />
                </div>
                <div className="text-4xl md:text-5xl font-bold text-primary mb-2">
                  {stat.number}
                </div>
                <div className="text-lg text-muted-foreground font-medium">
                  {stat.label}
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </section>
  );
};

export default Stats;

src/App.tsx.content:
import { Toaster } from "@/components/ui/toaster";
import { Toaster as Sonner } from "@/components/ui/sonner";
import { TooltipProvider } from "@/components/ui/tooltip";
import { QueryClientProvider } from "@tanstack/react-query";
import { Route, Switch } from "wouter";
import { CartProvider } from "@/contexts/CartContext";
import { AuthProvider } from "@/lib/auth-context";
import { queryClient } from "@/lib/queryClient";
import ProtectedRoute from "@/components/ProtectedRoute";
import Index from "./pages/Index";
import Platform from "./pages/Platform";
import MyRFQs from "./pages/MyRFQs";
import Login from "./pages/Login";
import RFQDetails from "./pages/RFQDetails";
import CompletedOrders from "./pages/CompletedOrders";
import NotFound from "./pages/NotFound";
import Dashboard from "./pages/admin/Dashboard";
import RFQManagement from "./pages/admin/RFQManagement";
import AddRFQ from "./pages/admin/AddRFQ";
import SupplierManagement from "./pages/admin/SupplierManagement";
import ClientManagement from "./pages/admin/ClientManagement";
import MaterialManagement from "./pages/admin/MaterialManagement";
import FinanceTracking from "./pages/admin/FinanceTracking";
import Reports from "./pages/admin/Reports";
import MarkupManagement from "./pages/admin/MarkupManagement";
import SettingsPage from "./pages/admin/Settings";
import UsersManagement from "./pages/admin/UsersManagement";
import SalesPipeline from "./pages/admin/SalesPipeline";
import Deals from "./pages/admin/Deals";
import Projects from "./pages/admin/Projects";

const App = () => (
  <QueryClientProvider client={queryClient}>
    <AuthProvider>
      <TooltipProvider>
        <CartProvider>
          <Toaster />
          <Sonner />
          <Switch>
            <Route path="/" component={Index} />
            <Route path="/platform" component={Platform} />
            <Route path="/my-rfqs" component={MyRFQs} />
            <Route path="/login" component={Login} />

            {/* Admin Routes - Protected */}
            <Route path="/admin">
              {() => (
                <ProtectedRoute>
                  <Dashboard />
                </ProtectedRoute>
              )}
            </Route>
            <Route path="/admin/rfqs">
              {() => (
                <ProtectedRoute allowedRoles={['admin', 'assistant']}>
                  <RFQManagement />
                </ProtectedRoute>
              )}
            </Route>
            <Route path="/admin/rfqs/new">
              {() => (
                <ProtectedRoute allowedRoles={['admin', 'assistant']}>
                  <AddRFQ />
                </ProtectedRoute>
              )}
            </Route>
            <Route path="/admin/rfq/:id">
              {(params) => (
                <ProtectedRoute allowedRoles={['admin', 'assistant']}>
                  <RFQDetails />
                </ProtectedRoute>
              )}
            </Route>
            <Route path="/admin/suppliers">
              {() => (
                <ProtectedRoute allowedRoles={['admin', 'procurement']}>
                  <SupplierManagement />
                </ProtectedRoute>
              )}
            </Route>
            <Route path="/admin/clients">
              {() => (
                <ProtectedRoute allowedRoles={['admin', 'sales']}>
                  <ClientManagement />
                </ProtectedRoute>
              )}
            </Route>
            <Route path="/admin/materials">
              {() => (
                <ProtectedRoute allowedRoles={['admin', 'procurement']}>
                  <MaterialManagement />
                </ProtectedRoute>
              )}
            </Route>
            <Route path="/admin/markup">
              {() => (
                <ProtectedRoute allowedRoles={['admin']}>
                  <MarkupManagement />
                </ProtectedRoute>
              )}
            </Route>
            <Route path="/admin/finance">
              {() => (
                <ProtectedRoute allowedRoles={['admin']}>
                  <FinanceTracking />
                </ProtectedRoute>
              )}
            </Route>
            <Route path="/admin/reports">
              {() => (
                <ProtectedRoute allowedRoles={['admin']}>
                  <Reports />
                </ProtectedRoute>
              )}
            </Route>
            <Route path="/admin/settings">
              {() => (
                <ProtectedRoute allowedRoles={['admin']}>
                  <SettingsPage />
                </ProtectedRoute>
              )}
            </Route>
            <Route path="/admin/users">
              {() => (
                <ProtectedRoute allowedRoles={['admin']}>
                  <UsersManagement />
                </ProtectedRoute>
              )}
            </Route>
            <Route path="/admin/pipeline">
              {() => (
                <ProtectedRoute allowedRoles={['admin', 'sales']}>
                  <SalesPipeline />
                </ProtectedRoute>
              )}
            </Route>
            <Route path="/admin/completed">
              {() => (
                <ProtectedRoute>
                  <CompletedOrders />
                </ProtectedRoute>
              )}
            </Route>
            <Route path="/admin/deals">
              {() => (
                <ProtectedRoute allowedRoles={['admin']}>
                  <Deals />
                </ProtectedRoute>
              )}
            </Route>
            <Route path="/admin/projects">
              {() => (
                <ProtectedRoute allowedRoles={['admin']}>
                  <Projects />
                </ProtectedRoute>
              )}
            </Route>

            {/* ADD ALL CUSTOM ROUTES ABOVE THE CATCH-ALL "*" ROUTE */}
            <Route component={NotFound} />
          </Switch>
        </CartProvider>
      </TooltipProvider>
    </AuthProvider>
  </QueryClientProvider>
);

export default App;

