@tailwind base;
@tailwind components;
@tailwind utilities;

/* Definition of the design system. All colors, gradients, fonts, etc should be defined here. 
All colors MUST be HSL.
*/

@layer base {
  :root {
    /* SOMVI Brand Colors */
    --background: 210 20% 98%; /* Light Gray #F9FAFB */
    --foreground: 215 25% 14%; /* Blue-Gray #1E293B */

    --card: 0 0% 100%; /* White */
    --card-foreground: 215 25% 14%;

    --popover: 0 0% 100%;
    --popover-foreground: 215 25% 14%;

    --primary: 215 25% 14%; /* Blue-Gray #1E293B */
    --primary-foreground: 0 0% 100%;

    --secondary: 210 20% 98%;
    --secondary-foreground: 215 25% 14%;

    --muted: 220 13% 91%;
    --muted-foreground: 215 16% 47%;

    --accent: 24 95% 53%; /* Orange #F97316 */
    --accent-foreground: 0 0% 100%;

    --destructive: 0 84.2% 60.2%;
    --destructive-foreground: 0 0% 100%;

    --border: 220 13% 91%;
    --input: 220 13% 91%;
    --ring: 24 95% 53%;

    --radius: 0.75rem;

    /* Custom gradients */
    --gradient-hero: linear-gradient(135deg, hsl(215 25% 14% / 0.95), hsl(215 25% 20% / 0.9));
    --gradient-accent: linear-gradient(135deg, hsl(24 95% 53%), hsl(24 95% 60%));

    /* Shadows */
    --shadow-soft: 0 2px 8px -2px hsl(215 25% 14% / 0.08);
    --shadow-medium: 0 8px 24px -4px hsl(215 25% 14% / 0.12);
    --shadow-strong: 0 16px 48px -8px hsl(215 25% 14% / 0.16);

    /* Animations */
    --transition-smooth: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    --transition-bounce: all 0.5s cubic-bezier(0.68, -0.55, 0.265, 1.55);

    --sidebar-background: 0 0% 98%;
    --sidebar-foreground: 240 5.3% 26.1%;
    --sidebar-primary: 240 5.9% 10%;
    --sidebar-primary-foreground: 0 0% 98%;
    --sidebar-accent: 240 4.8% 95.9%;
    --sidebar-accent-foreground: 240 5.9% 10%;
    --sidebar-border: 220 13% 91%;
    --sidebar-ring: 217.2 91.2% 59.8%;
  }

  .dark {
    --background: 215 25% 14%;
    --foreground: 0 0% 100%;
    --card: 215 25% 16%;
    --card-foreground: 0 0% 100%;
    --popover: 215 25% 16%;
    --popover-foreground: 0 0% 100%;
    --primary: 24 95% 53%;
    --primary-foreground: 0 0% 100%;
    --secondary: 215 25% 20%;
    --secondary-foreground: 0 0% 100%;
    --muted: 215 25% 20%;
    --muted-foreground: 220 9% 65%;
    --accent: 24 95% 53%;
    --accent-foreground: 0 0% 100%;
    --destructive: 0 62.8% 30.6%;
    --destructive-foreground: 0 0% 100%;
    --border: 215 25% 20%;
    --input: 215 25% 20%;
    --ring: 24 95% 53%;
    --sidebar-background: 240 5.9% 10%;
    --sidebar-foreground: 240 4.8% 95.9%;
    --sidebar-primary: 224.3 76.3% 48%;
    --sidebar-primary-foreground: 0 0% 100%;
    --sidebar-accent: 240 3.7% 15.9%;
    --sidebar-accent-foreground: 240 4.8% 95.9%;
    --sidebar-border: 240 3.7% 15.9%;
    --sidebar-ring: 217.2 91.2% 59.8%;
  }
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground font-sans antialiased;
  }

  html {
    scroll-behavior: smooth;
  }
}

@layer utilities {
  .section-container {
    @apply container mx-auto px-4 sm:px-6 lg:px-8;
  }

  .text-gradient {
    @apply bg-gradient-to-r from-primary to-accent bg-clip-text text-transparent;
  }

  .hover-lift {
    @apply transition-all duration-300 hover:-translate-y-1 hover:shadow-lg;
  }

  @keyframes gentle-pulse {
    0%, 100% {
      transform: scale(1);
      opacity: 1;
    }
    50% {
      transform: scale(1.05);
      opacity: 0.8;
    }
  }

  .animate-gentle-pulse {
    animation: gentle-pulse 3s ease-in-out infinite;
  }
}

