import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

src/pages/admin/SupplierManagement.tsx.content:
import { useState } from "react";
import { Plus, Edit, Trash2, Building2, Star, Phone, Mail, MessageSquare } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
  DialogDescription,
  DialogFooter,
} from "@/components/ui/dialog";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { useQuery, useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import AdminLayout from "@/components/admin/AdminLayout";
import type { Supplier } from "@shared/schema";

const SupplierManagement = () => {
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [editingSupplier, setEditingSupplier] = useState<Supplier | null>(null);
  const { toast } = useToast();

  const [formData, setFormData] = useState({
    name: "",
    company: "",
    contact: "",
    whatsapp: "",
    email: "",
    category: "",
    status: "active",
    rating: 5,
  });

  const { data: suppliers = [], isLoading } = useQuery<Supplier[]>({
    queryKey: ["/api/suppliers"],
  });

  const saveSupplierMutation = useMutation({
    mutationFn: async (data: any) => {
      const url = editingSupplier ? `/api/suppliers/${editingSupplier.id}` : "/api/suppliers";
      const method = editingSupplier ? "PATCH" : "POST";
      return await apiRequest(url, {
        method,
        body: JSON.stringify(data),
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/suppliers"] });
      toast({ 
        title: editingSupplier ? "Supplier Updated" : "Supplier Added",
        description: `${formData.name} has been ${editingSupplier ? "updated" : "added"} successfully.`,
      });
      resetForm();
    },
    onError: (error: any) => {
      toast({
        title: "Operation Failed",
        description: error.message || "Failed to save supplier.",
        variant: "destructive",
      });
    },
  });

  const deleteSupplierMutation = useMutation({
    mutationFn: async (id: number) => {
      return await apiRequest(`/api/suppliers/${id}`, {
        method: "DELETE",
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/suppliers"] });
      toast({ 
        title: "Supplier Deleted",
        description: "Supplier has been removed successfully.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Deletion Failed",
        description: error.message || "Failed to delete supplier.",
        variant: "destructive",
      });
    },
  });

  const categories = [
    "Cement & Concrete",
    "Steel & Metal",
    "Aggregates & Sand",
    "Finishing Materials",
    "Plumbing & Electrical",
    "Tools & Equipment",
    "Paint & Coating",
    "Timber & Wood",
    "General",
  ];

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.name) {
      toast({
        title: "Validation Error",
        description: "Supplier name is required.",
        variant: "destructive",
      });
      return;
    }

    saveSupplierMutation.mutate(formData);
  };

  const deleteSupplier = (id: number) => {
    deleteSupplierMutation.mutate(id);
  };

  const editSupplier = (supplier: Supplier) => {
    setEditingSupplier(supplier);
    setFormData({
      name: supplier.name,
      company: supplier.company || "",
      contact: supplier.contact || "",
      whatsapp: supplier.whatsapp || "",
      email: supplier.email || "",
      category: supplier.category || "",
      status: supplier.status || "active",
      rating: supplier.rating || 5,
    });
    setIsDialogOpen(true);
  };

  const resetForm = () => {
    setFormData({
      name: "",
      company: "",
      contact: "",
      whatsapp: "",
      email: "",
      category: "",
      status: "active",
      rating: 5,
    });
    setEditingSupplier(null);
    setIsDialogOpen(false);
  };

  const RatingStars = ({ rating }: { rating: number }) => {
    return (
      <div className="flex items-center gap-1">
        {[1, 2, 3, 4, 5].map((star) => (
          <Star
            key={star}
            className={`w-4 h-4 ${
              star <= rating
                ? "fill-yellow-400 text-yellow-400"
                : "fill-gray-200 text-gray-200"
            }`}
          />
        ))}
        <span className="text-sm text-muted-foreground ml-1">({rating}/5)</span>
      </div>
    );
  };

  if (isLoading) {
    return (
      <AdminLayout>
        <div className="section-container py-8 flex items-center justify-center">
          <p className="text-muted-foreground">Loading suppliers...</p>
        </div>
      </AdminLayout>
    );
  }

  return (
    <AdminLayout>
      <div className="section-container py-8">
        <Card>
          <CardHeader className="flex flex-row items-center justify-between">
            <div>
              <CardTitle>Supplier Management</CardTitle>
              <CardDescription className="mt-1">
                Manage supplier relationships and assign them to materials. Up to 5 suppliers per material.
              </CardDescription>
            </div>
            <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
              <DialogTrigger asChild>
                <Button onClick={() => resetForm()} data-testid="button-add-supplier">
                  <Plus className="w-4 h-4 mr-2" />
                  Add Supplier
                </Button>
              </DialogTrigger>
              <DialogContent className="max-w-lg">
                <DialogHeader>
                  <DialogTitle>
                    {editingSupplier ? "Edit Supplier" : "Add New Supplier"}
                  </DialogTitle>
                  <DialogDescription>
                    Enter supplier details. These suppliers can be assigned to materials with specific pricing.
                  </DialogDescription>
                </DialogHeader>
                <form onSubmit={handleSubmit} className="space-y-4">
                  {/* Supplier Name */}
                  <div>
                    <Label htmlFor="name">
                      Supplier Name <span className="text-destructive">*</span>
                    </Label>
                    <Input
                      id="name"
                      value={formData.name}
                      onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                      placeholder="e.g., ABC Construction Supplies"
                      required
                      data-testid="input-supplier-name"
                    />
                  </div>

                  {/* Company Name */}
                  <div>
                    <Label htmlFor="company">Company Name</Label>
                    <Input
                      id="company"
                      value={formData.company}
                      onChange={(e) => setFormData({ ...formData, company: e.target.value })}
                      placeholder="e.g., ABC Ltd."
                      data-testid="input-supplier-company"
                    />
                  </div>

                  {/* Contact Person */}
                  <div>
                    <Label htmlFor="contact">Contact Person</Label>
                    <Input
                      id="contact"
                      value={formData.contact}
                      onChange={(e) => setFormData({ ...formData, contact: e.target.value })}
                      placeholder="e.g., Mohamed Ahmed"
                      data-testid="input-supplier-contact"
                    />
                  </div>

                  {/* WhatsApp & Email */}
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <Label htmlFor="whatsapp">WhatsApp</Label>
                      <Input
                        id="whatsapp"
                        value={formData.whatsapp}
                        onChange={(e) => setFormData({ ...formData, whatsapp: e.target.value })}
                        placeholder="+252..."
                        data-testid="input-supplier-whatsapp"
                      />
                    </div>
                    <div>
                      <Label htmlFor="email">Email</Label>
                      <Input
                        id="email"
                        type="email"
                        value={formData.email}
                        onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                        placeholder="supplier@example.com"
                        data-testid="input-supplier-email"
                      />
                    </div>
                  </div>

                  {/* Category & Status */}
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <Label htmlFor="category">Category</Label>
                      <Select 
                        value={formData.category} 
                        onValueChange={(value) => setFormData({ ...formData, category: value })}
                      >
                        <SelectTrigger id="category" data-testid="select-supplier-category">
                          <SelectValue placeholder="Select category" />
                        </SelectTrigger>
                        <SelectContent>
                          {categories.map((cat) => (
                            <SelectItem key={cat} value={cat}>
                              {cat}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </div>
                    <div>
                      <Label htmlFor="status">Status</Label>
                      <Select 
                        value={formData.status} 
                        onValueChange={(value) => setFormData({ ...formData, status: value })}
                      >
                        <SelectTrigger id="status" data-testid="select-supplier-status">
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="active">Active</SelectItem>
                          <SelectItem value="inactive">Inactive</SelectItem>
                        </SelectContent>
                      </Select>
                    </div>
                  </div>

                  {/* Rating */}
                  <div>
                    <Label htmlFor="rating">Rating</Label>
                    <Select 
                      value={formData.rating.toString()} 
                      onValueChange={(value) => setFormData({ ...formData, rating: parseInt(value) })}
                    >
                      <SelectTrigger id="rating" data-testid="select-supplier-rating">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="5">⭐⭐⭐⭐⭐ (Excellent)</SelectItem>
                        <SelectItem value="4">⭐⭐⭐⭐ (Good)</SelectItem>
                        <SelectItem value="3">⭐⭐⭐ (Average)</SelectItem>
                        <SelectItem value="2">⭐⭐ (Below Average)</SelectItem>
                        <SelectItem value="1">⭐ (Poor)</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>

                  <DialogFooter>
                    <Button type="button" variant="outline" onClick={resetForm}>
                      Cancel
                    </Button>
                    <Button type="submit" disabled={saveSupplierMutation.isPending} data-testid="button-submit-supplier">
                      {saveSupplierMutation.isPending ? "Saving..." : editingSupplier ? "Update Supplier" : "Add Supplier"}
                    </Button>
                  </DialogFooter>
                </form>
              </DialogContent>
            </Dialog>
          </CardHeader>
          <CardContent>
            {suppliers.length === 0 ? (
              <div className="text-center py-12">
                <Building2 className="w-16 h-16 text-muted-foreground mx-auto mb-4" />
                <p className="text-lg font-medium mb-2">No Suppliers Yet</p>
                <p className="text-muted-foreground mb-4">Add suppliers to start assigning them to materials with pricing.</p>
                <Button onClick={() => setIsDialogOpen(true)} data-testid="button-add-first-supplier">
                  <Plus className="w-4 h-4 mr-2" />
                  Add Your First Supplier
                </Button>
              </div>
            ) : (
              <div className="overflow-x-auto">
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>Supplier</TableHead>
                      <TableHead>Company</TableHead>
                      <TableHead>Contact Info</TableHead>
                      <TableHead>Category</TableHead>
                      <TableHead>Rating</TableHead>
                      <TableHead>Status</TableHead>
                      <TableHead className="text-right">Actions</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {suppliers.map((supplier) => (
                      <TableRow key={supplier.id} data-testid={`row-supplier-${supplier.id}`}>
                        <TableCell>
                          <div>
                            <div className="font-medium" data-testid={`text-supplier-name-${supplier.id}`}>
                              {supplier.name}
                            </div>
                            {supplier.contact && (
                              <div className="text-sm text-muted-foreground">
                                {supplier.contact}
                              </div>
                            )}
                          </div>
                        </TableCell>
                        <TableCell>
                          {supplier.company ? (
                            <div className="flex items-center gap-2">
                              <Building2 className="w-4 h-4 text-muted-foreground" />
                              <span>{supplier.company}</span>
                            </div>
                          ) : (
                            <span className="text-muted-foreground">-</span>
                          )}
                        </TableCell>
                        <TableCell>
                          <div className="space-y-1">
                            {supplier.whatsapp && (
                              <div className="flex items-center gap-2 text-sm">
                                <MessageSquare className="w-3 h-3 text-green-600" />
                                <span>{supplier.whatsapp}</span>
                              </div>
                            )}
                            {supplier.email && (
                              <div className="flex items-center gap-2 text-sm">
                                <Mail className="w-3 h-3 text-blue-600" />
                                <span>{supplier.email}</span>
                              </div>
                            )}
                            {!supplier.whatsapp && !supplier.email && (
                              <span className="text-muted-foreground text-sm">No contact info</span>
                            )}
                          </div>
                        </TableCell>
                        <TableCell>
                          {supplier.category ? (
                            <Badge variant="secondary">{supplier.category}</Badge>
                          ) : (
                            <span className="text-muted-foreground">-</span>
                          )}
                        </TableCell>
                        <TableCell>
                          <RatingStars rating={supplier.rating || 5} />
                        </TableCell>
                        <TableCell>
                          <Badge 
                            variant={supplier.status === "active" ? "default" : "secondary"}
                            data-testid={`badge-supplier-status-${supplier.id}`}
                          >
                            {supplier.status === "active" ? "Active" : "Inactive"}
                          </Badge>
                        </TableCell>
                        <TableCell className="text-right">
                          <div className="flex justify-end gap-2">
                            <Button
                              size="sm"
                              variant="outline"
                              onClick={() => editSupplier(supplier)}
                              data-testid={`button-edit-supplier-${supplier.id}`}
                            >
                              <Edit className="w-4 h-4" />
                            </Button>
                            <AlertDialog>
                              <AlertDialogTrigger asChild>
                                <Button
                                  size="sm"
                                  variant="destructive"
                                  data-testid={`button-delete-supplier-${supplier.id}`}
                                >
                                  <Trash2 className="w-4 h-4" />
                                </Button>
                              </AlertDialogTrigger>
                              <AlertDialogContent>
                                <AlertDialogHeader>
                                  <AlertDialogTitle>Delete Supplier?</AlertDialogTitle>
                                  <AlertDialogDescription>
                                    This action cannot be undone. This will permanently delete {supplier.name} and remove all material assignments.
                                  </AlertDialogDescription>
                                </AlertDialogHeader>
                                <AlertDialogFooter>
                                  <AlertDialogCancel>Cancel</AlertDialogCancel>
                                  <AlertDialogAction onClick={() => deleteSupplier(supplier.id)}>
                                    Delete
                                  </AlertDialogAction>
                                </AlertDialogFooter>
                              </AlertDialogContent>
                            </AlertDialog>
                          </div>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </AdminLayout>
  );
};

export default SupplierManagement;

src/pages/Platform.tsx.content:
import { useState } from "react";
import { Link } from "wouter";
import { ShoppingCart, Search, FileText, Loader2, ChevronDown, ChevronRight, Plus, Minus } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import {
  Collapsible,
  CollapsibleContent,
  CollapsibleTrigger,
} from "@/components/ui/collapsible";
import { useCart } from "@/contexts/CartContext";
import Cart from "@/components/platform/Cart";
import { useToast } from "@/hooks/use-toast";
import { useQuery } from "@tanstack/react-query";

interface Material {
  id: number;
  nameEn: string;
  nameSo: string | null;
  unit: string | null;
  unitSo: string | null;
  category: string;
  subcategory: string | null;
  description: string | null;
  descriptionSo: string | null;
  imageUrl: string | null;
  active: boolean;
}

const Platform = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All");
  const [selectedSubcategory, setSelectedSubcategory] = useState<string | null>(null);
  const [showCart, setShowCart] = useState(false);
  const [showQuantityDialog, setShowQuantityDialog] = useState(false);
  const [selectedMaterial, setSelectedMaterial] = useState<Material | null>(null);
  const [quantity, setQuantity] = useState(1);
  const { addToCart, cartCount } = useCart();
  const { toast } = useToast();

  const { data: materials = [], isLoading } = useQuery<Material[]>({
    queryKey: ["/api/materials"],
  });

  const filteredMaterials = materials.filter((m) => {
    const matchesSearch =
      !searchTerm ||
      m.nameEn.toLowerCase().includes(searchTerm.toLowerCase()) ||
      (m.description && m.description.toLowerCase().includes(searchTerm.toLowerCase()));

    const matchesCategory = selectedCategory === "All" || m.category === selectedCategory;
    const matchesSubcategory = !selectedSubcategory || m.subcategory === selectedSubcategory;

    return matchesSearch && matchesCategory && matchesSubcategory;
  });

  // Group materials by category and subcategory for sidebar
  const categoriesWithSubs = materials.reduce((acc, material) => {
    if (!acc[material.category]) {
      acc[material.category] = new Set();
    }
    if (material.subcategory) {
      acc[material.category].add(material.subcategory);
    }
    return acc;
  }, {} as Record<string, Set<string>>);

  const handleOpenQuantityDialog = (material: Material) => {
    setSelectedMaterial(material);
    setQuantity(1);
    setShowQuantityDialog(true);
  };

  const handleAddToCart = () => {
    if (!selectedMaterial) return;

    const cartMaterial = {
      id: selectedMaterial.id.toString(),
      name: selectedMaterial.nameEn,
      description: selectedMaterial.description || "",
      unit: selectedMaterial.unit || "unit",
      category: selectedMaterial.category,
      image: selectedMaterial.imageUrl,
    };

    // Add with the specified quantity
    for (let i = 0; i < quantity; i++) {
      addToCart(cartMaterial);
    }

    toast({
      title: "Added to Cart",
      description: `${quantity} ${selectedMaterial.unit || 'unit'}(s) of ${selectedMaterial.nameEn} added to your cart.`,
    });

    setShowQuantityDialog(false);
    setSelectedMaterial(null);
    setQuantity(1);
  };

  const incrementQuantity = () => setQuantity(prev => prev + 1);
  const decrementQuantity = () => setQuantity(prev => Math.max(1, prev - 1));

  if (isLoading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="text-center">
          <Loader2 className="w-12 h-12 animate-spin text-primary mx-auto mb-4" />
          <p className="text-muted-foreground">Loading materials catalog...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <header className="sticky top-0 z-50 bg-primary text-primary-foreground shadow-lg border-b border-primary/10">
        <div className="section-container py-4 md:py-5">
          <div className="flex items-center justify-between gap-4">
            <Link to="/" className="flex items-center gap-2 hover:opacity-80 transition-opacity">
              <div className="w-8 h-8 md:w-10 md:h-10 bg-accent rounded-lg flex items-center justify-center">
                <span className="text-accent-foreground font-bold text-lg md:text-xl">S</span>
              </div>
              <span className="text-xl md:text-2xl font-bold">SOMVI Platform</span>
            </Link>
            <div className="flex items-center gap-2">
              <Link to="/my-rfqs">
                <Button
                  variant="outline"
                  className="hidden sm:flex border-accent text-accent hover:bg-accent hover:text-accent-foreground"
                  size="lg"
                >
                  <FileText className="w-4 h-4 mr-2" />
                  Track RFQs
                </Button>
              </Link>
              <Button
                onClick={() => setShowCart(true)}
                className="relative bg-accent hover:bg-accent/90 text-accent-foreground"
                size="lg"
              >
                <ShoppingCart className="w-5 h-5" />
                <span className="hidden sm:inline ml-2">Cart</span>
                {cartCount > 0 && (
                  <span className="absolute -top-2 -right-2 bg-primary text-primary-foreground rounded-full w-6 h-6 flex items-center justify-center text-xs font-bold shadow-lg">
                    {cartCount}
                  </span>
                )}
              </Button>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content with Sidebar */}
      <div className="section-container py-6 md:py-10">
        <div className="mb-6 animate-fade-in">
          <h1 className="text-2xl md:text-3xl font-bold mb-2 text-primary">
            Buy Construction Materials
          </h1>
          <p className="text-muted-foreground text-sm">
            Browse our wide selection of quality building materials
          </p>
        </div>

        {/* Search */}
        <div className="mb-6 animate-fade-in">
          <div className="relative max-w-xl">
            <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-muted-foreground" />
            <Input
              placeholder="Search for materials..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="pl-10 h-11 text-sm"
              data-testid="input-search-materials"
            />
          </div>
        </div>

        {/* Layout with Sidebar and Materials Grid */}
        <div className="flex flex-col lg:flex-row gap-6">
          {/* Category Sidebar */}
          <aside className="w-full lg:w-56 flex-shrink-0 space-y-1">
            <h2 className="font-semibold text-sm mb-3 text-primary uppercase tracking-wide">Products We Offer</h2>
            <Button
              variant={selectedCategory === "All" ? "default" : "ghost"}
              onClick={() => {
                setSelectedCategory("All");
                setSelectedSubcategory(null);
              }}
              className="w-full justify-start h-9 text-sm font-normal"
              data-testid="button-category-all"
            >
              All Products
            </Button>
            {Object.entries(categoriesWithSubs).map(([category, subcategories]) => (
              <Collapsible key={category} className="w-full">
                <CollapsibleTrigger asChild>
                  <Button
                    variant={selectedCategory === category ? "default" : "ghost"}
                    onClick={() => {
                      setSelectedCategory(category);
                      setSelectedSubcategory(null);
                    }}
                    className="w-full justify-between h-9 text-sm font-normal"
                    data-testid={`button-category-${category}`}
                  >
                    <span>{category}</span>
                    {subcategories.size > 0 && (
                      <ChevronDown className="w-4 h-4 ml-auto" />
                    )}
                  </Button>
                </CollapsibleTrigger>
                {subcategories.size > 0 && (
                  <CollapsibleContent className="pl-3 mt-1 space-y-0.5">
                    {Array.from(subcategories).map((sub) => (
                      <Button
                        key={sub}
                        variant={selectedSubcategory === sub ? "secondary" : "ghost"}
                        onClick={() => {
                          setSelectedCategory(category);
                          setSelectedSubcategory(sub);
                        }}
                        className="w-full justify-start text-xs h-8 font-normal text-muted-foreground hover:text-foreground"
                        data-testid={`button-subcategory-${sub}`}
                      >
                        {sub}
                      </Button>
                    ))}
                  </CollapsibleContent>
                )}
              </Collapsible>
            ))}
          </aside>

          {/* Materials Grid */}
          <main className="flex-1">
            <div className="mb-4">
              <p className="text-sm text-muted-foreground">
                Results for "{selectedCategory === "All" ? "All Products" : selectedCategory}"
              </p>
            </div>

            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
              {filteredMaterials.map((material) => (
                <Card key={material.id} className="overflow-hidden hover:shadow-lg transition-shadow duration-200 flex flex-col border-gray-200">
                  {material.imageUrl && (
                    <div className="h-44 overflow-hidden bg-gray-50 relative">
                      <img
                        src={material.imageUrl}
                        alt={material.nameEn}
                        className="w-full h-full object-cover"
                      />
                    </div>
                  )}
                  <CardHeader className="pb-2 pt-3 px-4">
                    <CardTitle className="text-sm font-semibold line-clamp-2 min-h-[2.5rem]">
                      {material.nameEn}
                    </CardTitle>
                  </CardHeader>
                  <CardContent className="px-4 pb-3 flex-grow">
                    {material.description && (
                      <p className="text-xs text-muted-foreground line-clamp-2 mb-2">
                        {material.description}
                      </p>
                    )}
                  </CardContent>
                  <CardFooter className="pt-0 px-4 pb-3">
                    <Button 
                      onClick={() => handleOpenQuantityDialog(material)} 
                      variant="outline"
                      className="w-full h-9 text-sm font-medium border-accent text-accent hover:bg-accent hover:text-accent-foreground"
                      data-testid={`button-add-material-${material.id}`}
                    >
                      Add
                    </Button>
                  </CardFooter>
                </Card>
              ))}
            </div>

            {filteredMaterials.length === 0 && (
              <div className="text-center py-12">
                <p className="text-muted-foreground text-lg">No materials match your search.</p>
              </div>
            )}
          </main>
        </div>
      </div>

      {/* Quantity Selector Dialog */}
      <Dialog open={showQuantityDialog} onOpenChange={setShowQuantityDialog}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle>Select Quantity</DialogTitle>
            <DialogDescription>
              {selectedMaterial && (
                <span className="text-foreground font-medium">
                  {selectedMaterial.nameEn}
                </span>
              )}
            </DialogDescription>
          </DialogHeader>

          {selectedMaterial && (
            <div className="flex flex-col gap-6 py-4">
              {/* Material Image and Info */}
              <div className="flex items-center gap-4">
                {selectedMaterial.imageUrl && (
                  <img
                    src={selectedMaterial.imageUrl}
                    alt={selectedMaterial.nameEn}
                    className="w-20 h-20 rounded-lg object-cover border"
                  />
                )}
                <div className="flex-1">
                  <h3 className="font-semibold text-base">{selectedMaterial.nameEn}</h3>
                  <p className="text-sm text-muted-foreground">
                    Unit: {selectedMaterial.unit || "unit"}
                  </p>
                  <Badge variant="secondary" className="mt-1 text-xs">
                    {selectedMaterial.category}
                  </Badge>
                </div>
              </div>

              {/* Quantity Selector */}
              <div className="space-y-2">
                <label className="text-sm font-medium">Quantity</label>
                <div className="flex items-center gap-3">
                  <Button
                    type="button"
                    variant="outline"
                    size="icon"
                    onClick={decrementQuantity}
                    disabled={quantity <= 1}
                    data-testid="button-decrease-quantity"
                  >
                    <Minus className="w-4 h-4" />
                  </Button>

                  <Input
                    type="number"
                    min="1"
                    value={quantity}
                    onChange={(e) => setQuantity(Math.max(1, parseInt(e.target.value) || 1))}
                    className="text-center text-lg font-semibold w-24"
                    data-testid="input-quantity"
                  />

                  <Button
                    type="button"
                    variant="outline"
                    size="icon"
                    onClick={incrementQuantity}
                    data-testid="button-increase-quantity"
                  >
                    <Plus className="w-4 h-4" />
                  </Button>
                </div>
                <p className="text-xs text-muted-foreground">
                  Total: {quantity} {selectedMaterial.unit || "unit"}(s)
                </p>
              </div>
            </div>
          )}

          <DialogFooter className="gap-2 sm:gap-0">
            <Button
              type="button"
              variant="outline"
              onClick={() => setShowQuantityDialog(false)}
              data-testid="button-cancel-quantity"
            >
              Cancel
            </Button>
            <Button
              type="button"
              onClick={handleAddToCart}
              className="bg-accent hover:bg-accent/90 text-accent-foreground"
              data-testid="button-confirm-add"
            >
              <ShoppingCart className="w-4 h-4 mr-2" />
              Add to RFQ
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Cart Drawer */}
      <Cart open={showCart} onClose={() => setShowCart(false)} />
    </div>
  );
};

export default Platform;

src/pages/admin/FinanceTracking.tsx.content:
import { useState, useEffect } from "react";
import { DollarSign, TrendingUp, Download } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, BarChart, Bar } from "recharts";
import AdminLayout from "@/components/admin/AdminLayout";
import { useToast } from "@/hooks/use-toast";
import * as XLSX from "xlsx";
import jsPDF from "jspdf";

const FinanceTracking = () => {
  const [financialData, setFinancialData] = useState({
    totalRevenue: 0,
    totalCosts: 0,
    totalProfit: 0,
    totalCommissions: 0,
  });
  const { toast } = useToast();

  useEffect(() => {
    const storedRFQs = JSON.parse(localStorage.getItem("rfqs") || "[]");
    const completedRFQs = storedRFQs.filter((rfq: any) => rfq.status === "Completed");

    let revenue = 0;
    let costs = 0;
    let profit = 0;

    completedRFQs.forEach((rfq: any) => {
      rfq.items?.forEach((item: any) => {
        if (item.suppliers) {
          Object.values(item.suppliers).forEach((supplier: any) => {
            const basePrice = supplier.basePrice || 0;
            const markup = supplier.markup || 0;
            const qty = item.quantity || 0;

            costs += basePrice * qty;
            revenue += (basePrice + markup) * qty;
            profit += markup * qty;
          });
        }
      });
    });

    setFinancialData({
      totalRevenue: revenue,
      totalCosts: costs,
      totalProfit: profit,
      totalCommissions: profit * 0.1, // 10% commission estimate
    });
  }, []);

  // Monthly profit data (last 6 months)
  const monthlyData = [
    { month: "Jan", profit: 12500, revenue: 45000, costs: 32500 },
    { month: "Feb", profit: 15200, revenue: 52000, costs: 36800 },
    { month: "Mar", profit: 13800, revenue: 48000, costs: 34200 },
    { month: "Apr", profit: 18300, revenue: 61000, costs: 42700 },
    { month: "May", profit: 16500, revenue: 55000, costs: 38500 },
    { month: "Jun", profit: 20100, revenue: 67000, costs: 46900 },
  ];

  const exportToExcel = () => {
    const data = [
      ["SOMVI Financial Report"],
      [""],
      ["Summary"],
      ["Total Revenue", financialData.totalRevenue],
      ["Total Costs", financialData.totalCosts],
      ["Total Profit", financialData.totalProfit],
      ["Total Commissions", financialData.totalCommissions],
      [""],
      ["Monthly Breakdown"],
      ["Month", "Revenue", "Costs", "Profit"],
      ...monthlyData.map((d) => [d.month, d.revenue, d.costs, d.profit]),
    ];

    const ws = XLSX.utils.aoa_to_sheet(data);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "Financial Report");
    XLSX.writeFile(wb, "SOMVI_Financial_Report.xlsx");

    toast({ title: "Excel report downloaded" });
  };

  const exportToPDF = () => {
    const doc = new jsPDF();

    doc.setFontSize(20);
    doc.text("SOMVI Financial Report", 20, 20);

    doc.setFontSize(12);
    doc.text("Summary", 20, 40);
    doc.text(`Total Revenue: $${financialData.totalRevenue.toFixed(2)}`, 20, 50);
    doc.text(`Total Costs: $${financialData.totalCosts.toFixed(2)}`, 20, 60);
    doc.text(`Total Profit: $${financialData.totalProfit.toFixed(2)}`, 20, 70);
    doc.text(`Total Commissions: $${financialData.totalCommissions.toFixed(2)}`, 20, 80);

    doc.save("SOMVI_Financial_Report.pdf");

    toast({ title: "PDF report downloaded" });
  };

  return (
    <AdminLayout>
      <div className="section-container py-8 space-y-8">
        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          <Card>
            <CardHeader className="flex flex-row items-center justify-between pb-2">
              <CardTitle className="text-sm font-medium">Total Revenue</CardTitle>
              <DollarSign className="w-4 h-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">${financialData.totalRevenue.toFixed(2)}</div>
              <p className="text-xs text-muted-foreground mt-1">From completed orders</p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between pb-2">
              <CardTitle className="text-sm font-medium">Total Costs</CardTitle>
              <DollarSign className="w-4 h-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">${financialData.totalCosts.toFixed(2)}</div>
              <p className="text-xs text-muted-foreground mt-1">Supplier costs</p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between pb-2">
              <CardTitle className="text-sm font-medium">Total Profit</CardTitle>
              <TrendingUp className="w-4 h-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-green-600">
                ${financialData.totalProfit.toFixed(2)}
              </div>
              <p className="text-xs text-muted-foreground mt-1">Net profit</p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between pb-2">
              <CardTitle className="text-sm font-medium">Commissions</CardTitle>
              <DollarSign className="w-4 h-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">${financialData.totalCommissions.toFixed(2)}</div>
              <p className="text-xs text-muted-foreground mt-1">Estimated 10%</p>
            </CardContent>
          </Card>
        </div>

        {/* Charts */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          <Card>
            <CardHeader>
              <CardTitle>Monthly Profit Trend</CardTitle>
            </CardHeader>
            <CardContent>
              <ResponsiveContainer width="100%" height={300}>
                <LineChart data={monthlyData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="month" />
                  <YAxis />
                  <Tooltip />
                  <Line type="monotone" dataKey="profit" stroke="hsl(var(--primary))" strokeWidth={2} />
                </LineChart>
              </ResponsiveContainer>
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle>Revenue vs Costs</CardTitle>
            </CardHeader>
            <CardContent>
              <ResponsiveContainer width="100%" height={300}>
                <BarChart data={monthlyData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="month" />
                  <YAxis />
                  <Tooltip />
                  <Bar dataKey="revenue" fill="hsl(var(--primary))" />
                  <Bar dataKey="costs" fill="hsl(var(--accent))" />
                </BarChart>
              </ResponsiveContainer>
            </CardContent>
          </Card>
        </div>

        {/* Export Options */}
        <Card>
          <CardHeader>
            <CardTitle>Export Financial Reports</CardTitle>
          </CardHeader>
          <CardContent className="flex gap-4">
            <Button onClick={exportToExcel}>
              <Download className="w-4 h-4 mr-2" />
              Export to Excel
            </Button>
            <Button onClick={exportToPDF} variant="outline">
              <Download className="w-4 h-4 mr-2" />
              Export to PDF
            </Button>
          </CardContent>
        </Card>
      </div>
    </AdminLayout>
  );
};

export default FinanceTracking;

src/pages/MyRFQs.tsx.content:
import { useState, useEffect } from "react";
import { Link } from "wouter";
import { ArrowLeft, FileText, Clock, CheckCircle, XCircle } from "lucide-react";
import { useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";

interface RFQ {
  id: string;
  date: string;
  client: {
    name: string;
    company: string;
    email: string;
    phone: string;
  };
  items: any[];
  status: "Pending" | "Quoted" | "Completed";
}

const MyRFQs = () => {
  const [searchPhone, setSearchPhone] = useState("");
  const [hasSearched, setHasSearched] = useState(false);

  // Fetch all RFQs from database
  const { data: allRFQs = [], isLoading } = useQuery<any[]>({
    queryKey: ["/api/rfqs"],
  });

  const handleSearch = () => {
    if (!searchPhone.trim()) {
      return;
    }
    setHasSearched(true);
  };

  // Filter RFQs by phone number
  const filteredRFQs = hasSearched && searchPhone.trim()
    ? allRFQs.filter((rfq: any) => {
        const cleanSearchPhone = searchPhone.replace(/\s/g, '').replace(/^\+/, '');
        const cleanRFQPhone = rfq.clientPhone.replace(/\s/g, '').replace(/^\+/, '');
        return cleanRFQPhone.includes(cleanSearchPhone) || cleanSearchPhone.includes(cleanRFQPhone);
      })
    : [];

  const getStatusIcon = (status: string) => {
    const lowerStatus = status?.toLowerCase();
    switch (lowerStatus) {
      case "pending":
        return <Clock className="w-4 h-4" />;
      case "quoted":
        return <FileText className="w-4 h-4" />;
      case "completed":
        return <CheckCircle className="w-4 h-4" />;
      default:
        return <XCircle className="w-4 h-4" />;
    }
  };

  const getStatusColor = (status: string) => {
    const lowerStatus = status?.toLowerCase();
    switch (lowerStatus) {
      case "pending":
        return "bg-yellow-500";
      case "quoted":
        return "bg-blue-500";
      case "completed":
        return "bg-green-500";
      default:
        return "bg-gray-500";
    }
  };

  return (
    <div className="min-h-screen bg-background">
      <header className="sticky top-0 z-50 bg-primary text-primary-foreground shadow-lg border-b border-primary/10">
        <div className="section-container py-4 md:py-5">
          <div className="flex items-center justify-between gap-4">
            <Link to="/platform" className="flex items-center gap-2 hover:opacity-80 transition-opacity">
              <div className="w-8 h-8 md:w-10 md:h-10 bg-accent rounded-lg flex items-center justify-center">
                <span className="text-accent-foreground font-bold text-lg md:text-xl">S</span>
              </div>
              <span className="text-xl md:text-2xl font-bold">My RFQs</span>
            </Link>
            <Link to="/platform">
              <Button className="bg-accent hover:bg-accent/90 text-accent-foreground" size="lg">
                <ArrowLeft className="w-4 h-4 mr-2" />
                Back to Platform
              </Button>
            </Link>
          </div>
        </div>
      </header>

      <main className="section-container py-6 md:py-10">
        <div className="max-w-4xl mx-auto">
          <div className="mb-8 animate-fade-in">
            <h1 className="text-3xl md:text-4xl font-bold mb-3 text-primary">
              Track Your RFQs
            </h1>
            <p className="text-muted-foreground">
              Enter your phone number to view your submitted quotation requests
            </p>
          </div>

          {/* Search Form */}
          <Card className="mb-8">
            <CardHeader>
              <CardTitle>Search Your RFQs</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                <div>
                  <Label htmlFor="phone">Phone Number</Label>
                  <Input
                    id="phone"
                    type="tel"
                    value={searchPhone}
                    onChange={(e) => setSearchPhone(e.target.value)}
                    placeholder="+252 615 401 195"
                    className="mt-1.5"
                    onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
                  />
                </div>
                <Button 
                  onClick={handleSearch} 
                  className="w-full bg-accent hover:bg-accent/90 text-accent-foreground"
                >
                  Search My RFQs
                </Button>
              </div>
            </CardContent>
          </Card>

          {/* Results */}
          {hasSearched && (
            <div className="animate-fade-in">
              {filteredRFQs.length === 0 ? (
                <Card>
                  <CardContent className="py-12 text-center">
                    <FileText className="w-12 h-12 mx-auto mb-4 text-muted-foreground" />
                    <p className="text-muted-foreground text-lg">No RFQs found for this phone number</p>
                    <p className="text-sm text-muted-foreground mt-2">
                      Make sure you entered the same number used during submission
                    </p>
                  </CardContent>
                </Card>
              ) : (
                <div className="space-y-4">
                  <h2 className="text-xl font-semibold mb-4">
                    Found {filteredRFQs.length} RFQ{filteredRFQs.length !== 1 ? 's' : ''}
                  </h2>
                  {filteredRFQs.map((rfq) => (
                    <Card key={rfq.id} className="hover:shadow-lg transition-shadow">
                      <CardContent className="p-4 md:p-6">
                        <div className="flex flex-col md:flex-row md:items-center justify-between gap-4">
                          <div className="flex-1">
                            <div className="flex items-center gap-2 mb-2">
                              <h3 className="font-bold text-lg">{rfq.id}</h3>
                              <Badge className={getStatusColor(rfq.status)}>
                                <span className="flex items-center gap-1">
                                  {getStatusIcon(rfq.status)}
                                  {rfq.status}
                                </span>
                              </Badge>
                            </div>
                            <p className="text-sm text-muted-foreground mb-1">
                              Submitted: {new Date(rfq.date).toLocaleDateString('en-US', { 
                                year: 'numeric', 
                                month: 'long', 
                                day: 'numeric' 
                              })}
                            </p>
                            <p className="text-sm text-muted-foreground">
                              Items: {rfq.items.length} material{rfq.items.length !== 1 ? 's' : ''}
                            </p>
                            {rfq.status === "Pending" && (
                              <p className="text-sm text-accent font-medium mt-2">
                                ⏳ Our team is reviewing your request
                              </p>
                            )}
                            {rfq.status === "Quoted" && (
                              <p className="text-sm text-blue-500 font-medium mt-2">
                                ✅ Quotation ready! Check your WhatsApp
                              </p>
                            )}
                            {rfq.status === "Completed" && (
                              <p className="text-sm text-green-600 font-medium mt-2">
                                ✅ Order completed
                              </p>
                            )}
                          </div>
                          <div className="flex flex-col gap-2">
                            <div className="text-right md:text-left">
                              <p className="text-xs text-muted-foreground">Total Items</p>
                              <p className="text-2xl font-bold text-primary">
                                {rfq.items.reduce((sum, item) => sum + item.quantity, 0)}
                              </p>
                            </div>
                          </div>
                        </div>

                        {/* Items List */}
                        <div className="mt-4 pt-4 border-t">
                          <p className="text-sm font-semibold mb-2">Materials:</p>
                          <div className="space-y-1">
                            {rfq.items.map((item, idx) => (
                              <div key={idx} className="text-sm text-muted-foreground flex justify-between">
                                <span>• {item.name}</span>
                                <span className="font-medium">
                                  {item.quantity} {item.unit}
                                </span>
                              </div>
                            ))}
                          </div>
                        </div>
                      </CardContent>
                    </Card>
                  ))}
                </div>
              )}
            </div>
          )}
        </div>
      </main>
    </div>
  );
};

export default MyRFQs;

