import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer } from "vite";

export function log(message: string) {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "2-digit",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [express] ${message}`);
}

export async function setupVite(app: Express, isProd: boolean) {
  if (isProd) {
    const distPath = path.resolve("dist");

    if (!fs.existsSync(distPath)) {
      throw new Error(
        `Could not find the production build. Please run 'npm run build' before starting the server in production mode.`,
      );
    }

    app.use(express.static(distPath, { index: false }));

    app.use((req, res, next) => {
      if (req.path.startsWith("/api")) {
        return next();
      }

      const indexPath = path.join(distPath, "index.html");
      res.sendFile(indexPath);
    });
  } else {
    const vite = await createViteServer({
      server: { middlewareMode: true },
      appType: "spa",
    });

    app.use(vite.middlewares);

    app.use(async (req, res, next) => {
      if (req.path.startsWith("/api")) {
        return next();
      }

      const url = req.originalUrl;

      try {
        let template = fs.readFileSync(
          path.resolve("index.html"),
          "utf-8",
        );

        template = await vite.transformIndexHtml(url, template);
        res.status(200).set({ "Content-Type": "text/html" }).end(template);
      } catch (e: any) {
        vite.ssrFixStacktrace(e);
        next(e);
      }
    });
  }
}

export function serveStatic(app: Express) {
  const distPath = path.resolve("dist");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the production build at ${distPath}. Please run 'npm run build' before starting the server.`,
    );
  }

  app.use(express.static(distPath));
  log(`Serving static files from ${distPath}`);
}

server/routes.ts.content:
import type { Express } from "express";
import { storage } from "./storage";
import { 
  insertClientSchema, 
  insertMaterialSchema, 
  insertRFQSchema, 
  insertRFQItemSchema,
  insertSupplierSchema,
  insertQuoteSchema,
  insertLeadSchema,
  insertSettingsSchema,
  insertMaterialSupplierSchema,
  insertUserSchema,
  insertDealSchema,
  insertProjectSchema,
  insertProjectPhaseSchema,
  insertProjectRfqSchema
} from "@shared/schema";
import { hashPassword, verifyPassword, generateToken, requireAuth, requireRole } from "./auth";

export function registerRoutes(app: Express) {

  // Authentication Routes
  app.post("/api/auth/login", async (req, res) => {
    try {
      const { email, password } = req.body;

      if (!email || !password) {
        return res.status(400).json({ error: "Email and password are required" });
      }

      const user = await storage.getUserByEmail(email);
      if (!user) {
        return res.status(401).json({ error: "Invalid credentials" });
      }

      if (!user.password) {
        return res.status(401).json({ error: "Invalid credentials" });
      }

      const isValid = await verifyPassword(password, user.password);

      if (!isValid) {
        return res.status(401).json({ error: "Invalid credentials" });
      }

      const token = generateToken({
        userId: user.id,
        email: user.email!,
        role: user.role,
      });

      res.cookie('authToken', token, {
        httpOnly: true,
        secure: process.env.NODE_ENV === 'production',
        sameSite: 'strict',
        maxAge: 7 * 24 * 60 * 60 * 1000, // 7 days
      });

      res.json({
        user: {
          id: user.id,
          name: user.name,
          email: user.email,
          role: user.role,
        },
      });
    } catch (error) {
      console.error("Login error:", error);
      res.status(500).json({ error: "Login failed" });
    }
  });

  app.post("/api/auth/logout", (req, res) => {
    res.clearCookie('authToken');
    res.json({ success: true });
  });

  app.get("/api/auth/me", requireAuth, async (req, res) => {
    try {
      const user = await storage.getUserById(req.user!.userId);
      if (!user) {
        return res.status(404).json({ error: "User not found" });
      }

      res.json({
        id: user.id,
        name: user.name,
        email: user.email,
        role: user.role,
      });
    } catch (error) {
      console.error("Get current user error:", error);
      res.status(500).json({ error: "Failed to get user" });
    }
  });

  // Users Management Routes (Admin only)
  app.get("/api/users", requireAuth, requireRole('admin'), async (req, res) => {
    try {
      const users = await storage.getUsers();
      res.json(users);
    } catch (error) {
      console.error("Error fetching users:", error);
      res.status(500).json({ error: "Failed to fetch users" });
    }
  });

  app.post("/api/users", requireAuth, requireRole('admin'), async (req, res) => {
    try {
      const ALLOWED_ROLES = ['admin', 'assistant', 'sales', 'procurement', 'logistics'];

      const validatedData = insertUserSchema.parse(req.body);

      // Validate role
      if (!ALLOWED_ROLES.includes(validatedData.role)) {
        return res.status(400).json({ error: "Invalid role. Allowed roles: " + ALLOWED_ROLES.join(', ') });
      }

      // Check for duplicate email
      if (validatedData.email) {
        const existingUser = await storage.getUserByEmail(validatedData.email);
        if (existingUser) {
          return res.status(400).json({ error: "A user with this email already exists" });
        }
      }

      if (validatedData.password) {
        validatedData.password = await hashPassword(validatedData.password);
      }

      const user = await storage.createUser(validatedData);
      res.json(user);
    } catch (error) {
      console.error("Error creating user:", error);
      res.status(400).json({ error: "Failed to create user" });
    }
  });

  app.patch("/api/users/:id", requireAuth, requireRole('admin'), async (req, res) => {
    try {
      const ALLOWED_ROLES = ['admin', 'assistant', 'sales', 'procurement', 'logistics'];
      const ALLOWED_FIELDS = ['name', 'email', 'password', 'role'];

      const id = parseInt(req.params.id);

      // Filter out non-allowed fields
      const updates: any = {};
      for (const key of Object.keys(req.body)) {
        if (ALLOWED_FIELDS.includes(key)) {
          updates[key] = req.body[key];
        }
      }

      // Validate role if provided
      if (updates.role && !ALLOWED_ROLES.includes(updates.role)) {
        return res.status(400).json({ error: "Invalid role. Allowed roles: " + ALLOWED_ROLES.join(', ') });
      }

      // Check for duplicate email if email is being updated
      if (updates.email) {
        const existingUser = await storage.getUserByEmail(updates.email);
        if (existingUser && existingUser.id !== id) {
          return res.status(400).json({ error: "A user with this email already exists" });
        }
      }

      // Hash password if provided
      if (updates.password) {
        updates.password = await hashPassword(updates.password);
      }

      // Validate partial schema
      const partialSchema = insertUserSchema.partial();
      const validatedUpdates = partialSchema.parse(updates);

      const user = await storage.updateUser(id, validatedUpdates);
      res.json(user);
    } catch (error) {
      console.error("Error updating user:", error);
      res.status(400).json({ error: "Failed to update user" });
    }
  });

